START TRANSACTION;
USE `gamedb`;
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (1, 'Jak and Daxter The Precursor Legacy', 2001, 'Jak and Daxter: The Precursor Legacy is a 2001 open world platform video game developed by Naughty Dog and published by Sony Computer Entertainment for the Sony PlayStation 2 on December 3, 2001, as the first game of the Jak and Daxter series.', 1, 'Jak', 'E');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (2, 'Jak II', 2003, 'Jak II (known as Jak II: Renegade in Oceania and Europe) is an open world platform third-person shooter action-adventure video game developed by Naughty Dog and published by Sony Computer Entertainment for the PlayStation 2. It is the second game of the Jak and Daxter series and is the sequel to Jak and Daxter: The Precursor Legacy. It was followed by Jak 3 a year later.', 1, 'Jak', 'T');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (3, 'Jak 3', 2004, 'Jak 3 is an open world platform third-person shooter action-adventure video game developed by Naughty Dog and published by Sony Computer Entertainment for the PlayStation 2. The game is the sequel to Jak II, and the third game in the series. The game picks up after the events of the previous games and the player takes on the dual role of recurring protagonists Jak and Daxter. It adds new weapons, devices and playable areas.', 1, 'Jak', 'T');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (4, 'Sly Cooper and the Thievius Raccoonus', 2002, 'Sly Cooper and the Thievius Raccoonus (known as Sly Raccoon in Europe and Australia) is a platform stealth video game created by Sucker Punch Productions that was released on the PlayStation 2 in 2002. The game was followed by three sequels, Sly 2: Band of Thieves, Sly 3: Honor Among Thieves and Sly Cooper: Thieves in Time. On November 9, 2010, Sly Cooper and the Thievius Raccoonus, as well as its first two sequels, were released together as The Sly Collection, a remastered port of all three games on a single Blu-ray as a Classics HD title for the PlayStation 3.', 1, 'Sly', 'E');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (5, 'Sly 2 Band of Thieves', 2004, 'Sly 2: Band of Thieves is a platform stealth video game developed by Sucker Punch Productions and published by Sony Computer Entertainment. It was released in 2004 for PlayStation 2. It is the sequel to the game Sly Cooper and the Thievius Raccoonus and part of the Sly Cooper video game series. The game received critical acclaim and is often considered to be one of the greatest PlayStation 2 games of all time', 1, 'Sly', 'E');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (6, 'Sly 3 Honor Among Thieves', 2005, 'Sly 3: Honor Among Thieves is a platform stealth video game developed by Sucker Punch Productions for PlayStation 2 in 2005. It is the third game in the Sly Cooper franchise. Sly 3 has optional 3-D stages, and comes with a pair of 3-D glasses inside the manual. The game received generally positive reviews from the gaming press. In 2010, Sly 3: Honor Among Thieves was released on PlayStation 3 as part of The Sly Collection.', 1, 'Sly', 'E10+');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (7, 'Sphinx and The Curse Mummy', 2003, 'Sphinx and the Cursed Mummy is a third person action-adventure video game inspired by the mythology of Ancient Egypt for GameCube, PlayStation 2 and Xbox. In Sphinx and the Cursed Mummy, the player falls into the role of a demi-god, Sphinx, and the undead corpse of Tutankhamen. Sphinx\'s role is one of a brave warrior who battles fearsome monsters and relies on raw power to complete tasks. Tutenkhanmen, also known as the Mummy, revolves around puzzle-solving and logical thinking to outwit his foes.', 1, 'Sphinx', 'T');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (8, 'Heroes of Might and Magic Quest for The Dragon Bone Staff', 2001, 'Darkness reigns. Sinister creatures stalk the innocent. King Argus III lies helpless and unconscious, dying from the assassination attempt that has left him poisoned and weak. Malazak, the revenge - driven son of the Wyrm - Father, awaits King Argus\'s death so he can divide the kingdom between himself, a demon, and a dark Minotaur. Only one Hero possesses the power to set things right. You must find the DragonBone Staff.', 1, 'Player', 'E');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (9, 'Ratchet and Clank', 2002, 'Ratchet & Clank is a 3D platform video game developed by Insomniac Games and published by Sony Computer Entertainment for the PlayStation 2 in 2002. Ratchet & Clank is the first game in the series and precedes Going Commando.\n\nThe game follows the anthropomorphic character Ratchet meeting the robot Clank on his home planet. Clank discovers that the villainous Chairman Drek of the Blarg race plans to create a new planet for his species, destroying other planets in the process. Clank convinces Ratchet to help him in his mission to gain the help of the famous superhero Captain Qwark.', 1, 'Ratchet', 'T');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (10, 'Ratchet & Clank: Going Commando', 2003, 'The game follows Ratchet and Clank, joined by newcomer Angela Cross, as they attempt to unravel a conspiracy behind a mysterious \"pet project,\" orchestrated by the shadowy MegaCorp.\n\nThe gameplay of Going Commando is similar to that of the original Ratchet & Clank. The player controlled protagonists are seen in a third-person perspective; the player uses various weapons and gadgets to defeat enemies and solve puzzles, while exploring planets, completing platforming sections and minigames, and progressing through the story. The game features many improvements over the original game, such as the aesthetics and introduces many new gameplay aspects, such as weapon upgrading.', 1, 'Ratchet', 'T');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (11, 'Ratchet & Clank: Up Your Arsenal\n', 2004, 'The gameplay is similar to previous games in the series but introduces new features such as a new control system and more levels in the upgrade system for weapons. It follows Ratchet and Clank\'s adventure through a fictional universe to defeat a robotic villain, Dr. Nefarious, who intends to destroy all organic life. Characters such as Dr. Nefarious and Sasha, a Cazar starship captain who helps Ratchet throughout the game, are introduced, and a number of characters return from the original Ratchet & Clank.', 1, 'Ratchet', 'T');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (12, 'Kingdom Hearts', 2002, 'The game combines characters and settings from Disney animated features with those from Square\'s Final Fantasy series, in addition to original characters and worlds created for the game. It follows the adventures of Sora, a cheerful teenager who fights against the forces of darkness alongside Donald Duck, Goofy and other Disney characters. The game was a departure from Square\'s standard role-playing games, introducing a substantial action and hack and slash element to the gameplay. The score was composed by Yoko Shimomura. Kingdom Hearts has an all-star voice cast and includes many of the Disney characters\' official voice actors.[2] It was longtime Square character designer Tetsuya Nomura\'s first time in a directorial position.', 1, 'Sora', 'E');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (13, 'Kingdom Hearts II\n', 2005, 'Kingdom Hearts II is the third game in the Kingdom Hearts series. It picks up one year after the events of Kingdom Hearts: Chain of Memories.[3] Sora, the protagonist of the first two games, returns to search for his lost friends while battling the sinister Organization XIII, a group of antagonists previously introduced in Chain of Memories.[4] Like the previous games, Kingdom Hearts II features a large cast of characters from Disney and Square Enix properties.\n\n', 1, 'Sora', 'E10+');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (14, 'God of War', 2005, 'God of War is an action-adventure game developed by Santa Monica Studio and published by Sony Computer Entertainment (SCE). First released on March 22, 2005, for the PlayStation 2 (PS2) console, it is the first installment in the series of the same name and the third chronologically. Loosely based on Greek mythology, it is set in ancient Greece with vengeance as its central motif. The player controls the protagonist Kratos, a Spartan warrior who serves the Olympian Gods. The goddess Athena tasks Kratos with killing Ares, the God of War and Kratos\' former mentor who tricked Kratos into killing his wife and daughter. As Ares besieges Athens out of hatred for Athena, Kratos embarks on a quest to find the one object capable of stopping the god once and for all: Pandora\'s Box.', 1, 'Kratos', 'M');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (15, 'God of War II', 2007, 'God of War II is an action-adventure game developed by Santa Monica Studio and published by Sony Computer Entertainment (SCE). First released for the PlayStation 2 console on March 13, 2007, it is the second installment in the God of War series, the sixth chronologically, and the sequel to 2005\'s God of War. The game is based on Greek mythology and set in ancient Greece, with vengeance as its central motif. The player character is protagonist Kratos, the new God of War who killed the former, Ares. Kratos is betrayed by Zeus, the King of the Olympian Gods, who strips him of his godhood and kills him. Slowly dragged to the Underworld, he is saved by the Titan Gaia, who instructs him to find the Sisters of Fate, as they can allow him to travel back in time, avert his betrayal, and take revenge on Zeus.', 1, 'Kratos', 'M');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (16, 'Max Payne', 2001, 'Max Payne is a neo-noir third-person shooter video game series developed by Remedy Entertainment (Max Payne and Max Payne 2) and Rockstar Studios (Max Payne 3). The series is named after its protagonist, Max Payne, a New York City police detective turned vigilante after his family was murdered by drug dealers. The series\' first and second installments were written by Sam Lake, while Max Payne 3 was primarily written by Rockstar Games\' Dan Houser.', 1, 'Max Payne', 'M');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (17, 'Scarface: The World Is Yours\n', 2006, 'Scarface: The World Is Yours is a 2006 open world action-adventure video game developed by Radical Entertainment for PlayStation 2, Xbox and Microsoft Windows published by Vivendi Games. The game is not a direct adaptation of the 1983 film directed by Brian De Palma, but is instead a broad strokes sequel which changes the ending of the film so that Tony Montana (originally played by Al Pacino) survives, and sets about exacting revenge on those who ousted him from power by re-establishing his drug empire in Miami. The game features Al Pacino\'s likeness in the character of Montana, but Pacino does not voice the character, as he and the game\'s producers felt his voice had changed too much since 1983. Instead, Montana is voiced by André Sogliuzzo, who was personally selected by Pacino himself. Actors from the original film who did voice work for the game include Steven Bauer, Robert Loggia, and Al Israel.', 1, 'Tony Montana', 'M');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (18, 'The Godfather', 2006, 'The game is based upon the 1972 film The Godfather and tells the story of a non-canon character, Aldo Trapani, whose rise through the ranks of the Corleone family intersects with the plot of the film on numerous occasions. His actions in the game often take the form of events that happened off screen in the film; for example, he avenges Bonasera\'s daughter, drives Vito Corleone to the hospital after he is shot, plants the gun for Michael Corleone to kill Sollozzo and Captain McCluskey, and places the horse\'s head in Jack Woltz\'s bed. Although the game was condemned by Francis Ford Coppola, who claimed Paramount never told him about its development or asked for his input, it does feature voice acting from several stars of the film, including James Caan as Sonny Corleone, Robert Duvall as Tom Hagen and Abe Vigoda as Salvatore Tessio. Marlon Brando also recorded dialogue for Vito Corleone, in what would be his final acting job, but his ill health made most of his recordings unusable.\n\nThe game is based upon the 1972 film The Godfather and tells the story of a non-canon character, Aldo Trapani, whose rise through the ranks of the Corleone family intersects with the plot of the film on numerous occasions. His actions in the game often take the form of events that happened off screen in the film; for example, he avenges Bonasera\'s daughter, drives Vito Corleone to the hospital after he is shot, plants the gun for Michael Corleone to kill Sollozzo and Captain McCluskey, and places the horse\'s head in Jack Woltz\'s bed. Although the game was condemned by Francis Ford Coppola, who claimed Paramount never told him about its development or asked for his input, it does feature voice acting from several stars of the film, including James Caan as Sonny Corleone, Robert Duvall as Tom Hagen and Abe Vigoda as Salvatore Tessio. Marlon Brando also recorded dialogue for Vito Corleone, in what would be his final acting job, but his ill health made most of his recordings unusable.\nThe Godfather is a 2006 open world action-adventure video game developed by EA Redwood Shores and published by Electronic Arts. Originally released in March 2006 for the Microsoft Windows, PlayStation 2, and the original Xbox. The game is based upon the 1972 film The Godfather and tells the story of a non-canon character, Aldo Trapani, whose rise through the ranks of the Corleone family intersects with the plot of the film on numerous occasions. His actions in the game often take the form of events that happened off screen in the film; for example, he avenges Bonasera\'s daughter, drives Vito Corleone to the hospital after he is shot, plants the gun for Michael Corleone to kill Sollozzo and Captain McCluskey, and places the horse\'s head in Jack Woltz\'s bed. Although the game was condemned by Francis Ford Coppola, who claimed Paramount never told him about its development or asked for his input, it does feature voice acting from several stars of the film, including James Caan as Sonny Corleone, Robert Duvall as Tom Hagen and Abe Vigoda as Salvatore Tessio. Marlon Brando also recorded dialogue for Vito Corleone, in what would be his final acting job, but his ill health made most of his recordings unusable.', 1, 'Aldo Trapani', 'M');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (19, 'SpiderMan', 2002, 'Spider-Man is a 2002 action-adventure video game based on the Marvel Comics character of the same name and the 2002 film of the same name.[2] Developed by Treyarch and published by Activision, it was released in 2002 for the Game Boy Advance, GameCube, Microsoft Windows, PlayStation 2, and Xbox. The game expands on the film\'s plot, featuring scenes and villains not present in the film. Tobey Maguire and Willem Dafoe reprised their roles from the movie, as Spider-Man and the Green Goblin, respectively, while Bruce Campbell, who played a wrestling ring announcer in the film, narrates the game\'s tutorial and bonus levels.', 1, 'Peter Parker', 'E');
INSERT INTO `Game` (`id`, `title`, `release_year`, `description`, `players`, `main_character`, `rating`) VALUES (20, 'Destroy All Humans!', 2005, 'Destroy All Humans! is a 2005 action-adventure video game developed by Pandemic Studios and published by THQ for PlayStation 2 and Xbox. The game is the first installment in the Destroy All Humans! franchise, and is set in 1959 in the United States, and parodies the lifestyles, pop culture, and political attitudes of this time period. The player controls Cryptosporidium 137 (a reference to the protozoan parasite Cryptosporidium), a member of the fictional Furon race of alien life, who has come to Earth to harvest DNA from humans to continue the cloning process of his species. Destroy All Humans! received a generally mixed to positive reception by critics. A full remake of the game was released on July 28, 2020.', 1, 'Cryptosporidium-136', NULL);

COMMIT;

